import { useState } from 'react'
export const TicTacToe = () => {
    const winningSets = [
        '036', '147', '358', '012', '345', '678', '048', '246'
    ]
    const squares = new Array(9).fill('.')
    const [xPlayer, setXPlayer] = useState('')
    const [oPlayer, setOPlayer] = useState('')
    const [plays, setPlays] = useState(0);
    const [picked, setPicked] = useState([])
    const [winner, setWinner] = useState('')

    const getWinner = (x, o) => {
        let wins;
        winningSets.forEach(wSet => {
            wins = !wins ? x.join('').indexOf(wSet) >= 0 ? 'X' : o.join('').indexOf(wSet) >= 0 ? 'O' : '' : wins;
        })
        if (wins) setWinner(wins + ' wins')
    }

    const handleClick = e => {
        const pick = e.target.id
        setPicked([...picked, pick])
        let newSet;
        const xPlays = xPlayer.split('');
        const oPlays = oPlayer.split('');
        if (plays % 2 === 0) {
            xPlays.push(pick);
            newSet = xPlayer + pick
            setXPlayer(newSet.split('').sort().join(''))
        } else {
            oPlays.push(pick);
            newSet = oPlayer + pick
            setOPlayer(newSet.split('').sort().join(''))
        }

        setPlays(prev => prev + 1);
        if (xPlays.length >= 3 || oPlays.length >= 3) getWinner(xPlays, oPlays);
    }

    return (
        <>
            <h1>Tic Tac Toe GAME</h1>
            <div className='game'>
                {squares.map((s, i) => <button
                    disabled={picked.includes(i.toString())}
                    key={i}
                    id={i} onClick={handleClick}>{i}</button>)}
            </div>
            <p>{winner}</p>
        </>
    )
}
