import { useState, useEffect } from 'react'
export const TicTacToe = () => {
    const winningSets = [
        '036', '147', '358', '012', '345', '678', '048', '246'
    ]
    const squares = new Array(9).fill('.')
    const [xPlayer, setXPlayer] = useState('')
    const [oPlayer, setOPlayer] = useState('')
    const [plays, setPlays] = useState(0);
    const [picked, setPicked] = useState([])
    const [winner, setWinner] = useState('')

    const getWinner = () => {
        let game;
        winningSets.forEach(wSet => {
            game = !game ? xPlayer.indexOf(wSet) >= 0 ? 'X' : oPlayer.indexOf(wSet) >= 0 ? 'O' : '' : game;
        })
        if (game) setWinner(game + ' wins')
    }
    const handleClick = e => {
        setPicked([...picked, e.target.id])
        let newSet, xPlays, oPlays;
        if (plays % 2 === 0) {
            xPlays = xPlayer.length + 1;
            newSet = xPlayer + e.target.id
            setXPlayer(newSet.split('').sort().join(''))
        } else {
            oPlays = oPlayer.length + 1;
            newSet = oPlayer + e.target.id
            setOPlayer(newSet.split('').sort().join(''))
        }
        setPlays(prev => prev + 1);
        if (xPlays >= 3 || oPlays >= 3) getWinner();
    }

    return (
        <>
            <h1>Tic Tac Toe GAME</h1>
            <div className='game'>
                {squares.map((s, i) => <button
                    disabled={picked.includes(i.toString())}
                    key={i}
                    id={i} onClick={handleClick}>{i}</button>)}
            </div>
            <p>{winner}</p>
        </>
    )
}
